import React, { useState,useEffect} from 'react'
import { Link } from 'react-router-dom'
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Favorite from '@material-ui/icons/Favorite';
import FavoriteBorder from '@material-ui/icons/FavoriteBorder';
import {FaHotel } from 'react-icons/fa';
import {MdFlight,MdFoodBank } from 'react-icons/md';
import {Button ,Modal} from 'react-bootstrap';

import './cards.css';
function Carditem(props) {
    const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);
return(
<div className="main">
<li className='cards__item'>
    <Link className='cards__item__link' to={props.path}>
    <figure className='cards__item__pic-wrap' data-category={props.label}>
        <img className='cards__item__img'
        alt='travel image'
        src={props.src}/>
        </figure>
        <div className='cards__item__info'>
        <MdFlight/>
    <FaHotel/>
   
   <MdFoodBank/>
            <h5 className='cards__item__text'>{props.text}</h5>
            
   <h5 className='cards__item__price'>Price:{props.price}</h5>
           
    <Button className="bbb" onClick={handleShow}>book now
      
       </Button>
       <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Booking for {props.text}</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>

        </div>
        </Link>
</li>
</div>
);
}
export default Carditem;
