import { react,useState } from "react";
import "@firebase/auth";
import "firebase/auth"
import {getAuth,createUserWithEmailAndPassword}
     from "firebase/auth";
import "../../App.css";
import {firebase,ss} from "../../Firebase";
import { Link } from "react-router-dom";
require("firebase/auth");

// const auth=firebase.getAuth();

function Login() {
  const [registerEmail, setRegisterEmail] = useState("");
  const [registerPassword, setRegisterPassword] = useState("");
  const [loginEmail, setLoginEmail] = useState("");
  const [loginPassword, setLoginPassword] = useState("");

  const [user, setUser] = useState({});

  ss.onAuthStateChanged(ss, (currentUser) => {
    setUser(currentUser);
  });

  const register = async () => {
    try {
      const user = await ss.createUserWithEmailAndPassword(
       
        registerEmail,
        registerPassword
      );
      console.log(user);
    } catch (error) {
      console.log(error.message);
    }
  };

  const login = async () => {
    try {
      const user = await ss.signInWithEmailAndPassword(
       
        loginEmail,
        loginPassword
      );
      console.log(user);

    } catch (error) {
      console.log(error.message);
    }
  };

  const logout = async () => {
    await ss.signOut(ss);
  };

  return (
    <div className="center">
      <div className="register">
        <h3 className="rtext"> Register User </h3>
        <input
          placeholder="Email..."
          onChange={(event) => {
            setRegisterEmail(event.target.value);
          }}
        />
        <input
          placeholder="Password..."
          onChange={(event) => {
            setRegisterPassword(event.target.value);
          }}
        />

        <button onClick={register}> Create User</button>
      </div>

      <div className="login">
        <h3 className="ltext"> Login </h3>
        <input
          placeholder="Email..."
          onChange={(event) => {
            setLoginEmail(event.target.value);
          }}
        />
        <input
          placeholder="Password..."
          onChange={(event) => {
            setLoginPassword(event.target.value);
          }}
        />

        <button onClick={login}>
          <Link to='/home'> Login</Link>
          </button>
      </div>

      <h4> User Logged In: </h4>
      {user?.email}

      <button onClick={logout}>
      <Link to='/login'> Sign Out </Link>
      </button>
    </div>
  );
}

export default Login;
